    private void btnIngresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIngresarActionPerformed
        if(txtUsuario.getText().equals("diego") && txtPassword.getText().equals("admin")){
            setVisible(false);
            new FormPanelProfesor().setVisible(true);
        }else{
            ImageIcon icono = new ImageIcon("error.png");
            JOptionPane.showMessageDialog(null, "<html><b style=\"color: red; font-size: 10px\">Incorrecto <br>Usuario o Contrase침a</b></html>","Show",JOptionPane.INFORMATION_MESSAGE,icono);
        }
    }//GEN-LAST:event_btnIngresarActionPerformed

    private void chkPasswordActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_chkPasswordActionPerformed
        if(chkPassword.isSelected()){
            txtPassword.setEchoChar((char)0);
        }else{
            txtPassword.setEchoChar('*');
        }
    }//GEN-LAST:event_chkPasswordActionPerformed

